import baseApi from "@/redux/api/baseApi";

export const balanceApi = baseApi.injectEndpoints({
  endpoints: (builder) => ({
    addBalance: builder.mutation({
      query: (balanceData) => {
        return {
          url: "/payment/add-balance",
          method: "POST",
          body: balanceData,
        };
      },
    }),
    myWallet: builder.query({
      query: () => {
        return {
          url: "/payment/my-wallat",
          method: "GET",
        };
      },
    }),
  }),
});

// Hooks generated by RTK Query for our endpoints
export const { useAddBalanceMutation, useMyWalletQuery } = balanceApi;
